import { App } from "./Components/fns";



export function noteReducer(state, action) {
  switch (action.type) {
    case "ADD_NOTE":
      return [...state, action.note];
    case "DELETE_NOTE":
      console.log(state, action)
      return state.filter(note => note._id !== action.id);
    case "UPDATE_NOTE":
      return state.map(note => {
        if (note.id === action.note.id) {
          return action.note;
        }
        return note;
      });
    case "SET_NOTES":
      return action.notes;
    default:
      return state;
  }
}

export default App;
