import React, { useState } from 'react'


function useCounter(init = 0) {
  const [count, setCount] = useState(init);

  const inc = () => {
    setCount(count + 1);
  }
  const dec = () => {
    setCount(count - 1);
  }

  return [count, inc, dec];
}

function useErrors(err = {}) {
  let obj = {};
  for (let e of err) {
    obj[e] = "";
  }
  const [errors, setErrors] = useState(obj);
  const setError = (name, value) => {
    setError({ ...errors, name: value });
  }
  return [errors, setError];
}

function Register() {
  const [emailError, setEmailError] = useState("");
  const [passwordError, setPasswordError] = useState("");
  const [count, inc, dec] = useCounter(5);
  const [errors, setError] = useErrors(['name', 'password', 'email'])
  return (
    <>
      <h1>{count}</h1>
      <button onClick={inc}>+</button>
      <button onClick={dec}>-</button>
      <form onSubmit={(evt)=>{
        evt.preventDefault();
        setError("email", "Hello email");
        setError("password", "---- HELLo ----");
      }}>
        <div className="row mb-3">
          <label htmlFor="inputEmail3" className="col-sm-2 col-form-label">
            Email
          </label>
          <div className="col-sm-10">
            <input
              required
              type="text"
              className="form-control"
              name="email"
              id="inputEmail3"
              onInput={() => {
              }}
            />
            {emailError}
          </div>
        </div>
        <div className="row mb-3">
          <label htmlFor="inputPassword3" className="col-sm-2 col-form-label">
            Password
          </label>
          <div className="col-sm-10">
            <input
              required
              name="password"
              type="password"
              className="form-control"
              id="inputPassword3"
              value="admin"
            />
            {passwordError}
          </div>
        </div>
        <button type="submit" className="btn btn-primary">
          Sign in
        </button>
      </form>
    </>
  )
}

export default Register
