import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'

function Note() {
    const [isUpdate, setIsUpdate] = useState(null);
    const [title, setTitle] = useState(note.title);
    const [content, setContent] = useState(note.content);
    const [error, setError] = useState("");
    const [note, setNote] = useState(null);

    const { id } = useParams();
    useEffect(()=>{
        loadNote();
    },[])



    const handleSubmit = async (evt) => {
        evt.preventDefault();
        let token = localStorage.getItem("token");
        if (!token) {
            return;
        }
        try {
            let res = await axios.put(`http://localhost:3345/note/update/${id}`, {
                title: title,
                content: content
            }, {
                headers: {
                    Authorization: `bearer ${token}`
                }
            })
            setError(res?.data.msg)
            setIsUpdate(false)

        } catch (err) {
            if (err.response?.data) {
                let data = err.response.data;
                if (data.name && data.name === 'unsuccess') {
                    setError(data.msg)
                }
                if (data.name && data.name === 'notauth') {
                    setError(data.msg)
                }
            }

        }


    }
    const loadNote = async (id) =>{
        // console.log(id)
        try{
          let res = await axios.get(`http://localhost:3345/note/${id}`);
          setNote(res.data)
          stopLoading();
        }catch(err){
          console.log(err)
        }
      }

    return (
        <>

            <div className="card m-2">
                <div className="card-body">
                    <h5 className="card-title">{note.title}</h5>
                    <p className="card-text">{note.content}</p>
                    <button type="button" className="btn btn-danger" onClick={() => { setIsUpdate(true) }}>
                        Edit
                    </button>
                </div>
            </div>
            {error ? <p>{error}</p> : ""}
            {isUpdate ? <form className='my-2' onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="title" className="form-label">Title</label>
                    <input type="text" className="form-control" id="title" value={title} onChange={(evt) => { setTitle(evt.target.value) }} />
                </div>
                <div className="mb-3">
                    <label htmlFor="content" className="form-label">Detail</label>
                    <textarea className="form-control" id="content" rows="8" value={content} onChange={(evt) => { setContent(evt.target.value) }} />
                </div>
                <button type="submit" className="btn btn-primary mx-2  ">Update Note</button>
                <button type="submit" className="btn btn-primary mx-2  " onClick={() => { setIsUpdate(false) }}>Close</button>
            </form> : ""}

        </>
    )
}

export default Note
