function ProtectRoute(props) {
  let auth = localStorage.getItem("token") ? true : false;
  // if(props.negate)
  // {
  //   auth = !auth;
  // }
  return (auth ? props.children : <Navigate to="/login" />);
}

function App() {
  return (
    <>

      <BrowserRouter>
        <Routes>
          <Route path="/" element={<RoutesLayout />} >
            {/* <Route path="" element={<ProtectRoute>
              <Home />
            </ProtectRoute>} />
            <Route path="/user" element={<ProtectRoute>
              <User />
            </ProtectRoute>} /> */}
            
            <Route path="login" element={<Login />} />
          </Route>
        </Routes>
      </BrowserRouter>
    </>
  )
}

export default App;
