import axios from 'axios';
import React, { useEffect, useState, useReducer } from 'react';
import { NavLink, useNavigate } from 'react-router-dom';


function noteReducer(state, action) {
  switch (action.type) {
    case "ADD_NOTE":
      return [...state, action.note];
    case "DELETE_NOTE":
      console.log(state, action)
      return state.filter(note => note._id !== action.id);
    case "UPDATE_NOTE":
      return state.map(note => {
        if (note.id === action.note.id) {
          return action.note;
        }
        return note;
      });
    case "SET_NOTES":
      return action.notes;
    default:
      return state;
  }
}
function Home({ loadNote }) {
  // console.log("==================")
  // its loading 4 times why ?
  // const [notes, setNotes] = useState(null);
  const [selectedNote, setSelectedNote] = useState(null)
  const navigate = useNavigate();

  const [notes, dispatch] = useReducer(noteReducer, null);

  function setNotes() {

  }


  useEffect(() => {
    console.log("USING EFFFECT");
    loadNotes();
  }, []);

  const loadNotes = async () => {
    console.log("USING hi");

    let token = localStorage.getItem("token");
    console.log(token);
    if (!token) {
      dispatch(null)
      return;
    }
    let res = await axios.get("http://localhost:3345/note", {
      headers: {
        Authorization: `bearer ${token}`
      }
    })
    console.log(res.data);

    dispatch({ type: "SET_NOTES", notes: res.data });
  }

  const deleteNote = async (id) => {
    //////
    let token = localStorage.getItem('token');
    if (!token) {
      return;
    }
    try {
      await axios.delete(`http://localhost:3345/note/delete/${id}`, {
        headers: {
          Authorization: `bearer ${token}`
        }
      });
      dispatch({ type: "DELETE_NOTE", id});
      // loadNotes();
      // setSelectedNote(null);
    } catch (err) {
      console.log(err)
    }
  }



  return (
    <>
      <div className="d-flex justify-content-between align-items-center">
        <h1> All Notes </h1>
        <NavLink className="btn btn-success addNote" to="/addnote">Add New Note</NavLink>
      </div>
      {!notes ? <h2>No Notes Aavailable</h2> :
        <div className="card-wrapper row justify-content-center">
          {notes.map((note, index) => {
            return (
              <div key={index} className="card m-2" style={{ width: "18rem" }}>
                <div className="card-body">
                  <h5 className="card-title" onClick={async () => { await loadNote(note._id); navigate(`/note/${note._id}`) }}>{note.title}</h5>
                  <p className="card-text">{note.content}</p>
                  <button type="button" className="btn btn-danger" onMouseEnter={() => { setSelectedNote(note) }} data-bs-toggle="modal" data-bs-target="#deleteNote" >
                    Remove
                  </button>
                </div>
              </div>

            )
          })}
          {selectedNote ? <div className="modal fade" id="deleteNote" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
            <div className="modal-dialog">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title" id="exampleModalLabel">{selectedNote.title}</h5>
                  <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                </div>
                <div className="modal-body">
                  <p>Do you really want to delete <strong>{selectedNote.title}</strong></p>
                </div>
                <div className="modal-footer">
                  <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                  <button type="button" className="btn btn-danger" onClick={() => { deleteNote(selectedNote._id) }} data-bs-dismiss="modal">Delete Note</button>
                </div>
              </div>
            </div>
          </div> : ""}

        </div>
      }

    </>
  );
}

export default Home;
